package com.pku.xiaoyoubang;import android.app.Activity;import android.content.Intent;import android.os.Bundle;import android.os.Handler;import android.os.Message;import android.view.Window;import com.pku.xiaoyoubang.tool.MyApplication;public class LogoActivity extends Activity{	private Handler handler;		protected void onCreate( Bundle savedInstanceState )	{		super.onCreate( savedInstanceState );		requestWindowFeature( Window.FEATURE_NO_TITLE );		MyApplication.getInstance().addActivity( this );				handler = new Handler()		{			public void handleMessage( Message message )			{				show();			}		};				initView();	}		private void initView()	{		setContentView( R.layout.start );				new Thread		(			new Thread()			{				public void run()				{					try					{						Thread.sleep( 3000 );					}					catch( Exception ex ) {}					handler.sendEmptyMessage( 0 );				}			}		).start();	}		private void show()	{		startActivity( new Intent( this, MainActivity.class ) );		finish();	}}