package com.pku.xiaoyoubang.entity;import java.io.Serializable;public class CommentEntity implements Serializable{	/**	 * 	 */	private static final long serialVersionUID = 1L;		private String id;	private String questionId;	private String replyId = "";	private String answerId = "";	private String userId;	private String userName;	private String replyName = "";	private String userHeadUrl;	private String time;	private String commentInfo;	/**	 *  0 : normal comment	 *  1 : act	 */	private int type = 0;		public String getId() {		return id;	}	public void setId(String id) {		this.id = id;	}	public String getQuestionId() {		return questionId;	}	public void setQuestionId(String questionId) {		this.questionId = questionId;	}	public String getReplyId() {		return replyId;	}	public void setReplyId(String replyId) {		this.replyId = replyId;	}	public String getAnswerId() {		return answerId;	}	public void setAnswerId(String answerId) {		this.answerId = answerId;	}	public String getUserId() {		return userId;	}	public void setUserId(String userId) {		this.userId = userId;	}	public String getUserName() {		return userName;	}	public void setUserName(String userName) {		this.userName = userName;	}	public String getReplyName() {		return replyName;	}	public void setReplyName(String replyName) {		this.replyName = replyName;	}	public String getUserHeadUrl() {		return userHeadUrl;	}	public void setUserHeadUrl(String userHeadUrl) {		this.userHeadUrl = userHeadUrl;	}	public String getTime() {		return time;	}	public void setTime(String time) {		this.time = time;	}	public String getCommentInfo() {		return commentInfo;	}	public void setCommentInfo(String commentInfo) {		this.commentInfo = commentInfo;	}	/**	 * @return the type	 */	public int getType() {		return type;	}	/**	 * @param type the type to set	 */	public void setType(int type) {		this.type = type;	}}