package com.pku.xiaoyoubang.entity;import java.io.Serializable;public class InviteUserEntity implements Serializable{	/**	 * 	 */	private static final long serialVersionUID = 1L;	private String id;	private String name;	private String headUrl;	private int sex;	private String pku;	private String company;	private String part;	private String job;	private boolean hasAnswered = false;	private boolean hasInvited = false;		public String getId() {		return id;	}	public void setId(String id) {		this.id = id;	}	public String getName() {		return name;	}	public void setName(String name) {		this.name = name;	}	public String getHeadUrl() {		return headUrl;	}	public void setHeadUrl(String headUrl) {		this.headUrl = headUrl;	}	public int getSex() {		return sex;	}	public void setSex(int sex) {		this.sex = sex;	}	public String getPku() {		return pku;	}	public void setPku(String pku) {		this.pku = pku;	}	public String getCompany() {		return company;	}	public void setCompany(String company) {		this.company = company;	}	public String getPart() {		return part;	}	public void setPart(String part) {		this.part = part;	}	public String getJob() {		return job;	}	public void setJob(String job) {		this.job = job;	}	public boolean isHasAnswered() {		return hasAnswered;	}	public void setHasAnswered(boolean hasAnswered) {		this.hasAnswered = hasAnswered;	}	public boolean isHasInvited() {		return hasInvited;	}	public void setHasInvited(boolean hasInvited) {		this.hasInvited = hasInvited;	}}