package com.pku.xiaoyoubang.model;import java.util.List;import android.content.Context;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.BaseAdapter;import android.widget.ImageView;import android.widget.TextView;import com.nostra13.universalimageloader.core.ImageLoader;import com.pku.xiaoyoubang.R;import com.pku.xiaoyoubang.entity.InviteUserEntity;import com.pku.xiaoyoubang.tool.Information;import com.pku.xiaoyoubang.tool.Tool;public class ChooseInviteListAdapter extends BaseAdapter{	private Context context;	private List< InviteUserEntity > list;	private ImageLoader imageLoader;		public ChooseInviteListAdapter( Context context, List< InviteUserEntity > list )	{		this.context = context;		this.list = list;				this.imageLoader = ImageLoader.getInstance();	}		public int getCount() 	{		return list.size();	}	public InviteUserEntity getItem( int position )	{		return list.get( position );	}	public long getItemId( int position ) 	{		return position;	}	public View getView( int position, View convertView, ViewGroup parent )	{		ViewHolder viewHolder;		if( convertView == null )		{			convertView = LayoutInflater.from( context ).inflate( R.layout.choose_invite_list_item, null );			viewHolder = new ViewHolder();			convertView.setTag( viewHolder );						viewHolder.imageHead = ( ImageView ) convertView.findViewById( R.id.choose_invite_list_item_head );			viewHolder.imageSex = ( ImageView ) convertView.findViewById( R.id.choose_invite_list_item_sex );			viewHolder.textName = ( TextView ) convertView.findViewById( R.id.choose_invite_list_item_name );			viewHolder.textSymbol = ( TextView ) convertView.findViewById( R.id.choose_invite_list_item_symbol );			viewHolder.textPku = ( TextView ) convertView.findViewById( R.id.choose_invite_list_item_pku );			viewHolder.textJob = ( TextView ) convertView.findViewById( R.id.choose_invite_list_item_job );		}		else		{			viewHolder = ( ViewHolder ) convertView.getTag();		}				InviteUserEntity entity = list.get( position );				imageLoader.displayImage( Information.Server_Url + entity.getHeadUrl(), viewHolder.imageHead, Information.options );		if( entity.getSex() == 0 )		{			viewHolder.imageSex.setImageResource( R.drawable.female_color );		}		else		{			viewHolder.imageSex.setImageResource( R.drawable.male_color );		}		viewHolder.textName.setText( entity.getName() );				if( entity.isHasAnswered() )		{			viewHolder.textSymbol.setText( "该用户已作答" );		}		else if( entity.isHasInvited() )		{			viewHolder.textSymbol.setText( "该用户已邀请" );		}		else if( entity.isHasJoined() )		{			viewHolder.textSymbol.setText( "该用户已报名" );		}		else		{			viewHolder.textSymbol.setText( "" );		}				viewHolder.textPku.setText( Tool.getFullPku( entity.getPku() ) );		String value = entity.getCompany() + "  ";		if( !entity.getPart().equals( "" ) )		{			value += entity.getPart() + "  ";		}		value += entity.getJob();		viewHolder.textJob.setText( value );				return convertView;	}		static class ViewHolder	{		ImageView imageHead;		ImageView imageSex;		TextView textName;		TextView textSymbol;		TextView textPku;		TextView textJob;	}}