package com.pku.xiaoyoubang.model;import java.util.List;import android.content.Context;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.BaseAdapter;import android.widget.ImageView;import android.widget.TextView;import com.nostra13.universalimageloader.core.DisplayImageOptions;import com.nostra13.universalimageloader.core.ImageLoader;import com.nostra13.universalimageloader.core.ImageLoaderConfiguration;import com.pku.xiaoyoubang.R;import com.pku.xiaoyoubang.entity.UserEntity;import com.pku.xiaoyoubang.tool.Information;import com.pku.xiaoyoubang.tool.Tool;public class FriendListAdapter extends BaseAdapter{	private Context context;	private List< UserEntity > list;	private ImageLoader imageLoader;	private DisplayImageOptions options;		public FriendListAdapter( Context context, List< UserEntity > list )	{		this.context = context;		this.list = list;		ImageLoaderConfiguration config = new ImageLoaderConfiguration.Builder( context ).build();		ImageLoader.getInstance().init( config );				this.options = new DisplayImageOptions.Builder()		.showImageOnLoading( R.drawable.head )		.showImageForEmptyUri( R.drawable.head )		.showImageOnFail( R.drawable.head )		.cacheInMemory( true )		.cacheOnDisk( true )		.build();		this.imageLoader = ImageLoader.getInstance();	}		public List< UserEntity > getList()	{		return list;	}		public int getCount() 	{		return list.size();	}	public Object getItem( int position )	{		return list.get( position );	}	public long getItemId( int position )	{		return position;	}	public View getView( int position, View convertView, ViewGroup parent ) 	{		ViewHolder viewHolder;		if( convertView == null )		{			convertView = LayoutInflater.from( context ).inflate( R.layout.friend_list_item, null );			viewHolder = new ViewHolder();			convertView.setTag( viewHolder );						viewHolder.headImage = ( ImageView ) convertView.findViewById( R.id.friend_list_item_head );			viewHolder.textName = ( TextView ) convertView.findViewById( R.id.friend_list_item_name );			viewHolder.imageSex = ( ImageView ) convertView.findViewById( R.id.friend_list_item_sex );			viewHolder.textPraise = ( TextView ) convertView.findViewById( R.id.friend_list_item_praise );			viewHolder.textPKU = ( TextView ) convertView.findViewById( R.id.friend_list_item_pku );			viewHolder.textJob = ( TextView ) convertView.findViewById( R.id.friend_list_item_job );		}		else		{			viewHolder = ( ViewHolder ) convertView.getTag();		}				UserEntity entity = list.get( position );		imageLoader.displayImage( Information.Server_Url + entity.getHeadUrl(), viewHolder.headImage, options );		viewHolder.textName.setText( entity.getName() );				if( entity.getSex() == 1 )		{			viewHolder.imageSex.setImageResource( R.drawable.male_color );		}		else		{			viewHolder.imageSex.setImageResource( R.drawable.female_color );		}		viewHolder.textPraise.setText( "èµž " + entity.getPraiseCount() );		viewHolder.textPKU.setText( Tool.getFullPku( entity.getPku() ) );		viewHolder.textJob.setText( entity.getJob1() + "  " + entity.getJob2() + "  " + entity.getJob3() );				return convertView;	}		static class ViewHolder	{		ImageView headImage;		TextView textName;		ImageView imageSex;		TextView textPraise;		TextView textPKU;		TextView textJob;	}}