package com.pku.xiaoyoubang.model;import java.util.List;import android.content.Context;import android.view.LayoutInflater;import android.view.View;import android.view.View.OnClickListener;import android.view.ViewGroup;import android.widget.BaseAdapter;import android.widget.TextView;import com.pku.xiaoyoubang.R;import com.pku.xiaoyoubang.entity.InviteEntity;import com.pku.xiaoyoubang.tool.Tool;public class InviteListAdapter1 extends BaseAdapter{	private Context context;	private List< InviteEntity > list;	private InviteListListener1 listener;		public InviteListAdapter1( Context context, List< InviteEntity > list )	{		this.context = context;		this.list = list;	} 		public int getCount() 	{		return list.size();	}	public Object getItem( int position ) 	{		return list.get( position );	}	public long getItemId( int position ) 	{		return position;	}	public View getView( final int position, View convertView, ViewGroup parent )	{		ViewHolder viewHolder;		if( convertView == null )		{			convertView = LayoutInflater.from( context ).inflate( R.layout.other_inivite_me_list_item, null );			viewHolder = new ViewHolder();			viewHolder.textName = ( TextView ) convertView.findViewById( R.id.other_invite_me_name );			viewHolder.textTime = ( TextView ) convertView.findViewById( R.id.other_invite_me_time );			viewHolder.textInviteInfo = ( TextView ) convertView.findViewById( R.id.other_invite_me_info );			viewHolder.line = ( View ) convertView.findViewById( R.id.other_invite_me_line );						convertView.setTag( viewHolder );		}		else		{			viewHolder = ( ViewHolder ) convertView.getTag(); 		}				InviteEntity entity = list.get( position );		viewHolder.textName.setText( entity.getName() + " " );		viewHolder.textName.setOnClickListener		(			new OnClickListener()			{				public void onClick( View view )				{					listener.clickUser( position );				}			}		);				viewHolder.textTime.setText( Tool.getShowTime( entity.getTime() ) );		viewHolder.textInviteInfo.setText( entity.getInviteInfo() );				if( position == list.size() - 1 )		{			viewHolder.line.setVisibility( View.INVISIBLE );		}		else		{			viewHolder.line.setVisibility( View.VISIBLE );		}				return convertView;	}		static class ViewHolder	{		TextView textName;		TextView textTime;		TextView textInviteInfo;		View line;	}		public void setMyListener( InviteListListener1 listener )	{		this.listener = listener;	}		public interface InviteListListener1	{		public void clickUser( int index );	}}