package com.pku.xiaoyoubang.model;import java.util.List;import android.content.Context;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.BaseAdapter;import android.widget.ImageView;import android.widget.TextView;import com.nostra13.universalimageloader.core.DisplayImageOptions;import com.nostra13.universalimageloader.core.ImageLoader;import com.nostra13.universalimageloader.core.ImageLoaderConfiguration;import com.pku.xiaoyoubang.R;import com.pku.xiaoyoubang.entity.InviteEntity;import com.pku.xiaoyoubang.tool.Information;import com.pku.xiaoyoubang.tool.Tool;public class InviteListAdapter2 extends BaseAdapter{	private Context context;	private List< InviteEntity > list;	private ImageLoader imageLoader;	private DisplayImageOptions options;		public InviteListAdapter2( Context context, List< InviteEntity > list )	{		this.context = context;		this.list = list;				ImageLoaderConfiguration config = new ImageLoaderConfiguration.Builder( context ).build();		ImageLoader.getInstance().init( config );				this.options = new DisplayImageOptions.Builder()		.showImageOnLoading( R.drawable.head )		.showImageForEmptyUri( R.drawable.head )		.showImageOnFail( R.drawable.head )		.cacheInMemory( true )		.cacheOnDisk( true )		.build();		this.imageLoader = ImageLoader.getInstance();	} 		public int getCount() 	{		return list.size();	}	public Object getItem( int position ) 	{		return list.get( position );	}	public long getItemId( int position ) 	{		return position;	}	public View getView( int position, View convertView, ViewGroup parent )	{		ViewHolder viewHolder;		if( convertView == null )		{			convertView = LayoutInflater.from( context ).inflate( R.layout.me_invite_other_list_item, null );			viewHolder = new ViewHolder();						viewHolder.headImage = ( ImageView ) convertView.findViewById( R.id.me_invite_other_head );			viewHolder.imageCheck = ( ImageView ) convertView.findViewById( R.id.me_invite_other_check );			viewHolder.textName = ( TextView ) convertView.findViewById( R.id.me_invite_other_name );			viewHolder.textTime = ( TextView ) convertView.findViewById( R.id.me_invite_other_time );			viewHolder.textInviteInfo = ( TextView ) convertView.findViewById( R.id.me_invite_other_info );			viewHolder.textSymbol = ( TextView ) convertView.findViewById( R.id.me_invite_other_symbol );			viewHolder.line = ( View ) convertView.findViewById( R.id.me_invite_other_line );						convertView.setTag( viewHolder );		}		else		{			viewHolder = ( ViewHolder ) convertView.getTag(); 		}				InviteEntity entity = list.get( position );		imageLoader.displayImage( Information.Server_Url + entity.getHeadUrl(), viewHolder.headImage, options );				viewHolder.textName.setText( "你邀请" + entity.getName() + "回答" );		viewHolder.textTime.setText( Tool.getShowTime( entity.getTime() ) );		viewHolder.textInviteInfo.setText( entity.getInviteInfo() );				if( entity.isHasAnswered() )		{			viewHolder.imageCheck.setVisibility( View.VISIBLE );			viewHolder.textSymbol.setVisibility( View.VISIBLE );			viewHolder.textSymbol.setText( "已作答" );		}		else		{			viewHolder.textSymbol.setVisibility( View.GONE );			viewHolder.imageCheck.setVisibility( View.INVISIBLE );		}				if( position == list.size() - 1 )		{			viewHolder.line.setVisibility( View.INVISIBLE );		}		else		{			viewHolder.line.setVisibility( View.VISIBLE );		}				return convertView;	}		static class ViewHolder	{		ImageView headImage;		ImageView imageCheck;		TextView textName;		TextView textTime;		TextView textInviteInfo;		TextView textSymbol;		View line;	}}