package com.pku.xiaoyoubang.model;import android.support.v4.app.Fragment;import android.support.v4.app.FragmentManager;import android.support.v4.app.FragmentPagerAdapter;import com.pku.xiaoyoubang.selfview.MyAnswerFragment;import com.pku.xiaoyoubang.selfview.MyMessageFragment;import com.pku.xiaoyoubang.selfview.MyQuestionFragment;import com.pku.xiaoyoubang.selfview.MySaveAnswerFragment;import com.viewpagerindicator.IconPagerAdapter;public class TabMeViewPagerAdapter extends FragmentPagerAdapter implements IconPagerAdapter{	public static final String[] TITLES = new String[] { "问过", "答过", "消息", "收藏" };	private int[] icons = { 0, 0, 0, 0 };	public TabMeViewPagerAdapter(FragmentManager fm)	{		super( fm );	}		public Fragment getItem( int index )	{		if( index == 0 )		{			return new MyQuestionFragment();		}		else if( index == 1 )		{			return new MyAnswerFragment();		}		else if( index == 2 )		{			return new MyMessageFragment();		}		else if( index == 3 )		{			return new MySaveAnswerFragment();		}		return null;	}	public CharSequence getPageTitle( int position )	{		return TITLES[ position % TITLES.length ];	}	public int getCount()	{		return TITLES.length;	}	public int getIconResId( int index ) 	{		return icons[ index ];	}		public void setIconResId( int index, int value )	{		icons[ index ] = value;	}}