package com.pku.xiaoyoubang.model;import android.support.v4.app.Fragment;import android.support.v4.app.FragmentManager;import android.support.v4.app.FragmentPagerAdapter;import com.pku.xiaoyoubang.selfview.AllActFragment;import com.pku.xiaoyoubang.selfview.AllQuestionFragment;import com.pku.xiaoyoubang.selfview.GoodAnswerFragment;import com.viewpagerindicator.IconPagerAdapter;public class ViewPagerAdapter extends FragmentPagerAdapter implements IconPagerAdapter{	public static final String[] TITLES = new String[] { "问题", "活动", "优答" };	private int[] icons = { 0, 0, 0 };	public ViewPagerAdapter(FragmentManager fm)	{		super( fm );	}		public Fragment getItem( int index )	{		if( index == 0 )		{			return new AllQuestionFragment();		}		else if( index == 1 )		{			return new AllActFragment();		}		else if( index == 2 )		{			return new GoodAnswerFragment();		}		return null;	}	public CharSequence getPageTitle( int position )	{		return TITLES[ position % TITLES.length ];	}	public int getCount()	{		return TITLES.length;	}	public int getIconResId( int index ) 	{		return icons[ index ];	}		public void setIconResId( int index, int value )	{		icons[ index ] = value;	}}