package com.pku.xiaoyoubang.model;import android.support.v4.app.Fragment;import android.support.v4.app.FragmentManager;import android.support.v4.app.FragmentPagerAdapter;import com.pku.xiaoyoubang.selfview.AllActFragment;import com.pku.xiaoyoubang.selfview.AllQuestionFragment;import com.pku.xiaoyoubang.selfview.GoodAnswerFragment;import com.viewpagerindicator.IconPagerAdapter;public class ViewPagerAdapter extends FragmentPagerAdapter implements IconPagerAdapter{	public static final String[] TITLES = new String[] { "问题", "活动", "优答" };	private int[] icons = { 0, 0, 0 };		private AllQuestionFragment fragment1 = null;	private AllActFragment fragment2 = null;	private GoodAnswerFragment fragment3 = null;	public ViewPagerAdapter(FragmentManager fm)	{		super( fm );	}		public Fragment getItem( int index )	{		if( index == 0 )		{			if( fragment1 == null )			{				fragment1 = new AllQuestionFragment();			}			return fragment1;		}		else if( index == 1 )		{			if( fragment2 == null )			{				fragment2 = new AllActFragment();			}			return fragment2;		}		else if( index == 2 )		{			if( fragment3 == null )			{				fragment3 = new GoodAnswerFragment();			}			return fragment3;		}		return null;	}	public CharSequence getPageTitle( int position )	{		return TITLES[ position % TITLES.length ];	}	public int getCount()	{		return TITLES.length;	}	public int getIconResId( int index ) 	{		return icons[ index ];	}		public void setIconResId( int index, int value )	{		icons[ index ] = value;	}}