package com.pku.xiaoyoubang.tool;import java.util.LinkedList;import java.util.List;import android.app.Activity;import android.app.Application;public class MyApplication extends Application{    private static MyApplication instance;    private List< Activity > activityList = new LinkedList< Activity >();    public MyApplication() {}        public synchronized static MyApplication getInstance()     {         if( instance == null )         {             instance = new MyApplication();         }         return instance;     }      public void addActivity( Activity activity )     {         activityList.add( activity );     }         public void logout()    {    	try         {             for( Activity activity : activityList )             {                if( activity != null )                    activity.finish();             }         }         catch( Exception ex )         {             ex.printStackTrace();         }         finally         {             System.exit( 0 );         }    }    public void onLowMemory()    {        super.onLowMemory();        System.gc();    }}