package com.pku.xiaoyoubang.view;import java.util.ArrayList;import java.util.List;import android.app.Activity;import android.os.Bundle;import android.view.View;import android.view.View.OnClickListener;import android.view.Window;import android.widget.Button;import android.widget.ImageView;import android.widget.ListView;import android.widget.TextView;import com.pku.xiaoyoubang.R;import com.pku.xiaoyoubang.entity.InviteEntity;import com.pku.xiaoyoubang.model.InviteListAdapter1;import com.pku.xiaoyoubang.model.InviteListAdapter2;import com.pku.xiaoyoubang.tool.MyApplication;import com.umeng.analytics.MobclickAgent;public class InviteInfoActivity extends Activity{	private Button buttonBack;		private TextView textTitle;	private TextView text1;	private TextView text2;	private ListView listView;	private ImageView imageCheck;		private List< InviteEntity > list;		@SuppressWarnings("unchecked")	protected void onCreate( Bundle savedInstanceState )	{		super.onCreate( savedInstanceState );		requestWindowFeature( Window.FEATURE_NO_TITLE );		MyApplication.getInstance().addActivity( this );				ArrayList<?> temp = getIntent().getExtras().getParcelableArrayList( "list" );		list = ( ArrayList< InviteEntity > ) temp.get( 0 );				initView( getIntent().getBooleanExtra( "isInviteMe", true ), getIntent().getBooleanExtra( "hasAnswered", false ) );	}		private void initView( boolean isInviteMe, boolean hasAnswered )	{		setContentView( R.layout.invite_info );		buttonBack = ( Button ) findViewById( R.id.invite_info_button_back );		buttonBack.setText( "<  " );		buttonBack.setOnClickListener		(			new OnClickListener()			{				public void onClick( View view )				{					back();				}			}		);		textTitle = ( TextView ) findViewById( R.id.invite_info_title );		text1 = ( TextView ) findViewById( R.id.invite_info_text1 );		text2 = ( TextView ) findViewById( R.id.invite_info_text2 );		listView = ( ListView ) findViewById( R.id.invite_info_list );		imageCheck = ( ImageView ) findViewById( R.id.invite_info_check );				if( isInviteMe )		{			textTitle.setText( "受邀详情" );			text1.setText( "受邀 " + list.size() );			if( hasAnswered )			{				imageCheck.setVisibility( View.VISIBLE );				text2.setText( "已作答" );			}			listView.setAdapter( new InviteListAdapter1( this, list ) );		}		else		{			textTitle.setText( "邀请详情" );			text1.setText( "邀请 " + list.size() );			listView.setAdapter( new InviteListAdapter2( this, list ) );		}	}		private void back()	{		finish();	}		public void onResume() 	{		super.onResume();		MobclickAgent.onResume( this );	}		public void onPause()	{		super.onPause();		MobclickAgent.onPause( this );	}}