package com.pku.xiaoyoubang.view;import android.app.Activity;import android.os.Bundle;import android.view.View;import android.view.View.OnClickListener;import android.view.Window;import android.widget.Button;import android.widget.TextView;import com.pku.xiaoyoubang.R;import com.pku.xiaoyoubang.tool.MyApplication;public class UserIntroActivity extends Activity{	private String name;	private String value;		private TextView textTitle;	private TextView textValue;		private Button buttonBack;		protected void onCreate( Bundle savedInstanceState )	{		super.onCreate( savedInstanceState );		requestWindowFeature( Window.FEATURE_NO_TITLE );		MyApplication.getInstance().addActivity( this );				name = getIntent().getStringExtra( "name" );		value = getIntent().getStringExtra( "value" );				initView();	}		private void initView()	{		setContentView( R.layout.user_intro );				textTitle = ( TextView ) findViewById( R.id.user_intro_title );		textTitle.setText( name + "的自我介绍" );				textValue = ( TextView ) findViewById( R.id.user_intro_text );		textValue.setText( value );				buttonBack = ( Button ) findViewById( R.id.user_intro_button_back );		buttonBack.setText( "<  " );		buttonBack.setOnClickListener		(			new OnClickListener()			{				public void onClick( View view )				{					finish();				}			}		);	}}